mapper.select:
SELECT id, name, code, description, order_num, enabled, create_time, update_time
FROM subject
WHERE id = #{id}

SELECT id, name, code, description, order_num, enabled, create_time, update_time
FROM subject
WHERE code = #{code}

SELECT COUNT(*)
FROM subject
WHERE name = #{name}

    AND id != #{excludeId}

SELECT COUNT(*)
FROM subject
WHERE code = #{code}

    AND id != #{excludeId}
